'use strict';

const {cut} = require('./cut');

const mapN = (list, step, fn) => Array.from(cut(list, step), fn);
const toObject = (list, fn) => Object.fromEntries(mapN(list, 2, fn));
const toMap = (list, fn) => new Map(mapN(list, 2, fn));

exports.mapN = mapN;
exports.toObject = toObject;
exports.toMap = toMap;
