'use strict';

const {slice} = Array.prototype;

function* everyN(list, step){
	for(let i=0, n=list.length; i<n; i+=step){
		yield slice.call(list, i, i+step);
	}
}

const mapN = (list, step, fn) => Array.from(everyN(list, step), fn);
const toObject = (list, fn) => Object.fromEntries(mapN(list, 2, fn));
const toMap = (list, fn) => new Map(mapN(list, 2, fn));

exports.everyN = everyN;
exports.mapN = mapN;
exports.toObject = toObject;
exports.toMap = toMap;
