'use strict';

function calcCount(value, key='prev'){
	let count = 0;
	while(value){
		++count;
		value = value[key];
	}
	return count;
}

function collect(value, key='prev'){
	let result = [];
	while(value){
		result.push(value)
		value = value[key];
	}
	return result;
}

exports.calcCount = calcCount;
exports.collect = collect;
