
function calcOffset(list){
	const result = [];
	list.reduce((s, v) => {
		result.push(s);
		return s + v.length;
	}, 0);
	return result;
}

function calcSize(list){
	const result = [];
	list.reduce((s, v) => {
		s += v.length;
		result.push(s);
		return s;
	}, 0);
	return result;
}

exports.calcOffset = calcOffset;
exports.calcSize = calcSize;
