'use strict';

const revert = f => (a, b) => f(b, a);
const collator = f => k => (a, b) => f(a[k], b[k]);

const min = collator((a, b) => a - b);
const numeric = collator(Intl.Collator(undefined, {numeric:true}).compare);
const zh = collator(Intl.Collator('zh').compare);

exports.min = min;
exports.numeric = numeric;
exports.zh = zh;
