"use strict";

function range(value){
	let result = [];
	for(let i=0; i<value; ++i){
		result[i] = i;
	}
	return result;
}

function* combination(list, offset=0){
	if(offset >= list.length){
		yield [];
		return;
	}
	for(let tail of combination(list, offset+1)){
		for(let head of list[offset]){
			yield [head, ...tail];
		}
	}
}

module.exports = {
	range,
	combination
};