"use strict";

function compare(a, b, handler){
	for(let key in a){
		if(!(key in b)){
			handler(-1, key, a, b);
		}else if(a[key] != b[key]){
			handler(0, key, a, b);
		}
	}
	for(let key in b){
		if(!(key in a)){
			handler(1, key, a, b);
		}
	}
}

module.exports = {
	compare
};