exports.Add = (a, b) => a + b;
exports.Subtract = (a, b) => a - b;
exports.Multiply = (a, b) => a * b;
exports.Divide = (a, b) => a / b;
exports.Modulus = (a, b) => a % b;
exports.Not = (value) => !value;
exports.And = (a, b) => a && b;
exports.Or = (a, b) => a || b;
exports.Equal = (a, b) => a == b;
exports.NotEqual = (a, b) => a != b;
exports.GreaterEqual = (a, b) => a >= b;
exports.GreaterThan = (a, b) => a > b;
exports.LessEqual = (a, b) => a <= b;
exports.LessThan = (a, b) => a < b;

exports.Double = v => v * 2;
exports.Square = v => v * v;
exports.IsOdd = v => v % 2 == 1;
