
function CenterX(target, targetWidth, parentWidth, offsetX=0){
	target.x = offsetX + 0.5 * (parentWidth - targetWidth);
}

function CenterY(target, targetHeight, parentHeight, offsetY=0){
	target.y = offsetY + 0.5 * (parentHeight - targetHeight);
}

function Center(target, targetWidth, targetHeight, parentWidth, parentHeight, offsetX=0, offsetY=0){
	CenterX(target, targetWidth, parentWidth, offsetX);
	CenterY(target, targetHeight, parentHeight, offsetY);
}

function CenterViewX(target, parentWidth, offsetX=0){
	CenterX(target, target.width, parentWidth, offsetX);
}

function CenterViewY(target, parentHeight, offsetY=0){
	CenterY(target, target.height, parentHeight, offsetY);
}

function CenterView(target, parentWidth, parentHeight, offsetX=0, offsetY=0){
	CenterViewX(target, parentWidth, offsetX);
	CenterViewY(target, parentHeight, offsetY);
}

function Bottom(target, targetHeight, parentHeight, offsetY=0){
	target.y = offsetY + parentHeight - targetHeight;
}

function Right(target, targetWidth, parentWidth, offsetX=0){
	target.x = offsetX + parentWidth - targetWidth;
}

function BottomRight(target, targetWidth, targetHeight, parentWidth, parentHeight, offsetX=0, offsetY=0){
	Bottom(target, targetHeight, parentHeight, offsetY);
	Right(target, targetWidth, parentWidth, offsetX);
}

function AlignRegionX(outer, inner, ratio=0.5){
	return ratio * (outer.width - inner.width);
}

function AlignRegionY(outer, inner, ratio=0.5){
	return ratio * (outer.height - inner.height);
}

function AlignRegion(outer, inner, result, ratioX=0.5, ratioY=0.5){
	result.x = AlignRegionX(outer, inner, ratioX);
	result.y = AlignRegionY(outer, inner, ratioY);
}
