
function add(obj, k, v){
	if(k in obj){
		obj[k].push(v);
	}else{
		obj[k] = [v];
	}
}

function del(obj, k, v){
	if(k in obj){
		let array = obj[k];
		let index = array.indexOf(v);
		if(index >= 0){
			array.splice(index, 1);
			return true;
		}
	}
	return false;
}

function has(obj, k, v){
	return (k in obj) && (obj[k].includes(v));
}

function addOnce(obj, k, v){
	if(has(obj, k, v))
		return false;
	add(obj, k, v);
	return true;
}

export{
	add,
	del,
	has,
	addOnce
};