
const list = [
	[ 3000,		.03],
	[12000,		.1 ],
	[25000,		.2 ],
	[35000,		.25],
	[55000,		.3 ],
	[80000,		.35],
	[0,			.45],
].map(([a, b]) => [a * 12, b]);

function compute(value){
	let result = 0;
	let offset = 0;
	for(let [a, b] of list){
		if(!a || value <= a)
			return result + (value - offset) * b;
		result += (a - offset) * b;
		offset = a;
	}
}

const calc = value => Array(12).fill(0)
	.map((_, i) => compute(value * (i + 1)))
	.map((v, i, a) => i ? v - a[i-1] : v);

void function(){
	let value = 282.78;
	let result = calc(value);
	console.log(result, result.reduce((a,b)=>a+b));
}();