"use strict";

const escapeColor = (pattern => {
	return input => {
		while(input.search(pattern) >= 0)
			input = input.replace(pattern, "<font color='$1'>$2</font>");
		return input;
	};
})(/\[(#[0-9a-fA-F]{1,6})\s(.+?)\]/);

function formatInt(value, length=2, radix=10){
	return value.toString(radix).toUpperCase().padStart(length, "0");
}

function upperCaseFirst(source){
	if(null == source || source.length < 1)return source;
	if(1 == source.length)return source.toUpperCase();
	return source.charAt(0).toUpperCase() + source.slice(1);
}

module.exports = {
	escapeColor,
	formatInt,
	upperCaseFirst,
};