
function findBody(text, start, pairA='{', pairB='}'){
	let count = 0;
	for(let i=start, n=text.length; i<n; ++i){
		const char = text.charAt(i);
		if(char === pairA){
			++count;
		}else if(char === pairB){
			--count;
			if(count === 0)return text.slice(start, i+1);
		}
	}
}

module.exports = findBody;
