const walkTree = (onEnter, onLeave) => function fn(item, selfFlag=true, ...args){
	if(selfFlag && onEnter?.call(this, item, ...args))return;
	const {children} = item;
	for(let i=0, n=children.length; i<n; ++i){
		fn.call(this, children[i], true, ...args);
	}
	if(selfFlag && onLeave?.call(this, item, ...args))return;
}

exports.walkTree = walkTree;
