'use strict';

class Range
{
	static Create(baseVal, varVal=0){
		const halfVarVal = 0.5 * varVal;
		return new Range(baseVal - halfVarVal, baseVal + halfVarVal);
	}
	
	constructor(beginValue, endValue){
		this.setValue(beginValue, endValue);
	}
	
	get endValue(){
		return this.beginValue + this.valueChanged;
	}
	
	setValue(beginValue, endValue){
		this.beginValue = beginValue;
		this.valueChanged = endValue - beginValue;
	}
	
	getValue(ratio){
		return this.beginValue + this.valueChanged * ratio;
	}
	
	getRandowValue(){
		return this.getValue(Math.random());
	}
	
	flip(){
		this.beginValue += this.valueChanged;
		this.valueChanged = -this.valueChanged;
	}
}

module.exports = Range;
