"use strict";

const weight = [7, 9, 10, 5, 8, 4, 2, 1, 6, 3];

function verifyID(id){
	if(!/^\d{17}[Ⅹ\d]$/.test(id)){
		return false;
	}
	let v = id.slice(0, -1).split("").map((v, i) => parseInt(v) * weight[i % weight.length]).reduce((a, b) => a + b);
	return "10Ⅹ98765432".charAt(v % 11) == id.slice(-1);
}

module.exports = verifyID;
