"use strict";

function create([tagName, ...children]){
	let attributes = Array.isArray(children[0]) ? null : children.shift();
	let result = "<" + tagName;
	if(attributes)
		result += Object.entries(attributes).map(([k,v]) => ` ${k}="${v}"`).join("");
	if(children.length > 0)
		return result + ">" + children.map(create).join("") + `</${tagName}>`;
	return result + "/>";
}

module.exports = {
	create
};