'use strict';

require('./CodeView.css');

const {createElement} = require('../createElement');
const escape = require('utils/html/escape');

const CodeView = createElement(function(tag, end, {children, className, props}){
	tag('div', {...props, className: className ? `${className} editor`: 'editor'})
		for(let code of children.split('\n')){
			tag('code', {key:code}), tag`${escape(code)}`, end('code')
		}
	end('div')
});

module.exports = CodeView;
