'use strict';

require('./Collapse.css');

const {createElement} = require('../createElement');
const Details = require('./Details');

const Collapse = createElement(function(tag, end, {style, children}){
	tag('div', {className:'collapse', style})
		React.Children.map(children, child => {
			const {title, open} = child.props;
			const children = React.cloneElement(child, {title:null, open:null});
			tag(Details, {title, open, children}, true)
		});
	end('div')
});

module.exports = Collapse;
