
require('./Video.css');

const {createElement} = require('../createElement');

const Video = createElement(function(tag, end, {style}){
	tag('div', {className:'video', style})
		tag('video', {controls:true, controlslist:'nofullscreen', disablePictureInPicture:true}, true)
		tag('canvas', true)
	end('div')
});

module.exports = Video;
