'use strict';

class Texture2D
{
	constructor(rawTexture){
		this.rawTexture = rawTexture;
		this.xyuvMul = [1, 1, 1, 1];
		this.xyuvAdd = [0, 0, 0, 0];
		this.scale9grid = [0, 0, 0, 0];//left, right, top, bottom
		if(rawTexture){
			this.width = rawTexture.width;
			this.height = rawTexture.height;
		}
	}

	getRawAsset(){
		return this.rawTexture;
	}

	setUV(rawWidth, rawHeight, rect, flipY=true){
		const {x, y, width, height} = rect;
		const {xyuvMul, xyuvAdd} = this;
		this.width = width;
		this.height = height;
		xyuvMul[2] = width / rawWidth;
		xyuvAdd[2] = x / rawWidth;
		if(flipY){
			xyuvMul[3] = -height / rawHeight;
			xyuvAdd[3] = (y + height) / rawHeight;
		}else{
			xyuvMul[3] = height / rawHeight;
			xyuvAdd[3] = y / rawHeight;
		}
	}
}

module.exports = Texture2D;
