'use strict';

const DisplayObject = require('./DisplayObject');
const {Matrix2D} = require('opengl/math');
const RADIAN = Math.PI / 180;

class DisplayObject2D extends DisplayObject
{
	constructor(){
		super(Matrix2D);

		this._isLocalMatrixDirty = true;

		this._x = 0.0;
		this._y = 0.0;
		this._scaleX = 1.0;
		this._scaleY = 1.0;
		this._rotation = 0.0;
	}

	get x(){return this._x;}
	get y(){return this._y;}
	get scaleX(){return this._scaleX;}
	get scaleY(){return this._scaleY;}
	get rotation(){return this._rotation;}
	
	set x(value){
		if(this._x == value)
			return;
		this._x = value;
		this.onTransformChanged();
	}
	
	set y(value){
		if(this._y == value)
			return;
		this._y = value;
		this.onTransformChanged();
	}
	
	set scaleX(value){
		if(this._scaleX == value)
			return;
		this._scaleX = value;
		this.onTransformChanged();
	}
	
	set scaleY(value){
		if(this._scaleY == value)
			return;
		this._scaleY = value;
		this.onTransformChanged();
	}
	
	set scale(value){
		if(this._scaleX == value && this._scaleY == value)
			return;
		this._scaleX = this._scaleY = value;
		this.onTransformChanged();
	}
	
	set rotation(value){
		if(this._rotation == value)
			return;
		this._rotation = value;
		this.onTransformChanged();
	}

	onTransformChanged(){
		this._isLocalMatrixDirty = true;
	}

	get transform(){
		if(this._isLocalMatrixDirty){
			Matrix2D.compose(this._localMatrix,
				this._scaleX, this._scaleY,
				this._rotation * RADIAN,
				this._x, this._y
			);
			this._isLocalMatrixDirty = false;
		}
		return this._localMatrix;
	}

	addChild(child){
		return this.addChildAt(child, this.numChildren);
	}

	removeChild(child){
		let index = this.getChildIndex(child);
		if(index >= 0)
			return this.removeChildAt(index);
	}

	addChildAt(child, index){
		console.assert(0 <= index && index <= this.numChildren);

		if(child.parent){
			if(child.parent === this){
				this.setChildIndex(child, Math.min(index, this.numChildren-1));
				return child;
			}
			child.parent.removeChild(child);
		}

		this.children.splice(index, 0, child);
		child._parent = this;

		return child;
	}

	removeChildAt(index){
		console.assert(0 <= index && index < this.numChildren);
		let child = this._children.splice(index, 1)[0];
		child._parent = null;
		return child;
	}

	getChildAt(index){
		console.assert(0 <= index && index < this.numChildren);
		return this._children[index];
	}

	getChildIndex(child){
		return this._children.indexOf(child);
	}

	setChildIndex(child, index){
		console.assert(0 <= index && index < this.numChildren);
		let prevIndex = this.getChildIndex(child);
		if(prevIndex < 0 || prevIndex === index)return;
		let {_children} = this;
		_children.splice(prevIndex, 1);
		_children.splice(index, 0, child);
	}

	swapChildren(child1, child2){
		this.swapChildrenAt(this.getChildIndex(child1), this.getChildIndex(child2));
	}

	swapChildrenAt(index1, index2){
		if(index1 === index2)return;
		let {_children} = this;
		let child1 = _children[index1];
		let child2 = _children[index2];
		if(child1 && child2){
			_children[index1] = child2;
			_children[index2] = child1;
		}
	}

	get bottomChild(){
		return this.getChildAt(0);
	}

	get topChild(){
		return this.getChildAt(this.numChildren-1);
	}

	swapChildToTop(child){
		let childIndex = getChildIndex(child);
		if(childIndex < 0)return;
		let lastIndex = this.numChildren - 1;
		if(childIndex < lastIndex){
			this.swapChildrenAt(childIndex, lastIndex);
		}
	}

	swapChildToBottom(child){
		let childIndex = this.getChildIndex(child);
		if(childIndex > 0){
			this.swapChildrenAt(childIndex, 0);
		}
	}
}

module.exports = DisplayObject2D;
