exports.Bitmap3D = function(width, height, lens){
	let bitmap3D = new DisplayObject2D();
	bitmap3D.type = 'bitmap3d';
	bitmap3D.root3d = new DisplayObject3D();
	bitmap3D.width = width;
	bitmap3D.height = height;
	bitmap3D.camera = new Camera3D(lens);
	bitmap3D.texture = new Texture2D();
	return bitmap3D;
}
