'use strict';

const Vulkan = require('./Vulkan');

module.exports = Vulkan;

const TextManager = require('../managers/TextManager');
const VertexBufferManager = require('../managers/VertexBufferManager');
const TransformUniformManager = require('../managers/TransformUniformManager');
const DirectBufferManager = require('../managers/DirectBufferManager');
const MouseManager = require('../managers/MouseManager');
const Camera3DGPUResourceManager = require('../managers/Camera3DGPUResourceManager');

const prepareDraw2D = require('../render2d/helper/prepareDraw2D');
const onBatchDraw2D = require('../render2d/helper/onBatchDraw2D');

Vulkan.registerManager('textMgr', TextManager);
Vulkan.registerManager('transformUniformMgr', TransformUniformManager);
Vulkan.registerManager('vertexBufferMgr', VertexBufferManager);
Vulkan.registerManager('directBufferMgr', DirectBufferManager);
Vulkan.registerManager('mouseMgr', MouseManager);
Vulkan.registerManager('camera3DGPUResourceMgr', Camera3DGPUResourceManager);

Vulkan.registerManager('prepareDraw2D', prepareDraw2D);
Vulkan.registerManager('onBatchDraw2D', onBatchDraw2D);
