'use strict';

const Vulkan = require('./Vulkan');

module.exports = Vulkan;

const TextManager = require('../managers/TextManager');
const VertexBufferManager = require('../managers/VertexBufferManager');
const TransformUniformManager = require('../managers/TransformUniformManager');
const DirectBufferManager = require('../managers/DirectBufferManager');
const MouseManager = require('../managers/MouseManager');
const Camera3DGPUResourceManager = require('../managers/Camera3DGPUResourceManager');

const prepareDraw2D = require('../render2d/helper/prepareDraw2D');
const onBatchDraw2D = require('../render2d/helper/onBatchDraw2D');

const initBindGroups = require('../pipelines/initBindGroups');
const initPipelines = require('../pipelines/initPipelines');

Vulkan.registerManagerFactory('bindGroupLayoutDict', initBindGroups);
Vulkan.registerManagerFactory('pipelineDict', initPipelines);

Vulkan.registerManagerFactory('textMgr', TextManager);
Vulkan.registerManagerFactory('transformUniformMgr', TransformUniformManager);
Vulkan.registerManagerFactory('vertexBufferMgr', VertexBufferManager);
Vulkan.registerManagerFactory('directBufferMgr', DirectBufferManager);
Vulkan.registerManagerFactory('mouseMgr', MouseManager);
Vulkan.registerManagerFactory('camera3DGPUResourceMgr', Camera3DGPUResourceManager);

Vulkan.registerManagerFactory('prepareDraw2D', prepareDraw2D);
Vulkan.registerManagerFactory('onBatchDraw2D', onBatchDraw2D);
