
const Vector3D = require('webgpu/math/Vector3D');

class AABB2
{
	constructor(){
		this.center = new Vector3D();
		this.halfWidth = 0;
		this.halfHeight = 0;
	}
	getProjLen(axis){
		const {halfWidth, halfHeight} = this;
		return halfWidth * Math.abs(axis.x) + halfHeight * Math.abs(axis.y);
	}
}

module.exports = AABB2;
