
function calcVertexBound(vertexList, outputBound, countPerElement=3){
	let minX=Number.MAX_VALUE, maxX=Number.MIN_VALUE;
	let minY=Number.MAX_VALUE, maxY=Number.MIN_VALUE;
	let minZ=Number.MAX_VALUE, maxZ=Number.MIN_VALUE;
	
	for(let i=0, n:int=vertexList.length; i<n; i+=countPerElement){
		let vx = vertexList[i];
		let vy = vertexList[i+1];
		let vz = vertexList[i+2];
		
		if(vx < minX)minX = vx;
		if(vx > maxX)maxX = vx;
		if(vy < minY)minY = vy;
		if(vy > maxY)maxY = vy;
		if(vz < minZ)minZ = vz;
		if(vz > maxZ)maxZ = vz;
	}
	outputBound.setMinMax(minX, minY, minZ, maxX, maxY, maxZ);
}

exports.calcVertexBound = calcVertexBound;
