function union2(a, b, result){
	if(a.isEmpty()){
		if(result != b){
			result.copyFrom(b);
		}
		return;
	}
	if(b.isEmpty()){
		if(result != a){
			result.copyFrom(a);
		}
		return;
	}
	var minX = Math.min(a.minX, b.minX);
	var minY = Math.min(a.minY, b.minY);
	var minZ = Math.min(a.minZ, b.minZ);
	var maxX = Math.max(a.maxX, b.maxX);
	var maxY = Math.max(a.maxY, b.maxY);
	var maxZ = Math.max(a.maxZ, b.maxZ);
	result.setMinMax(minX, minY, minZ, maxX, maxY, maxZ);
}

function union3(a, b, result){
	if(a.isEmpty()){
		if(result != b){
			result.copyFrom(b);
		}
		return;
	}
	if(b.isEmpty()){
		if(result != a){
			result.copyFrom(a);
		}
		return;
	}
	var minX = Math.min(a.x, b.x);
	var minY = Math.min(a.y, b.y);
	var maxX = Math.max(a.x + a.width, b.x + b.width);
	var maxY = Math.max(a.y + a.height, b.y + b.height);
	result.x = minX;
	result.y = minY;
	result.width = maxX - minX;
	result.height = maxY - minY;
}