'use strict';

class DrawUnitCollector2D
{
	constructor(){
		this.bitmapList = [];
		this.bitmap3DList = [];
		this.filterList = [];
	}

	clear(){
		this.bitmapList.length = 0;
		this.bitmap3DList.length = 0;
		this.filterList.length = 0;
	}

	addBitmap(value){
		this.bitmapList.push(value);
	}

	addBitmap3D(value){
		this.bitmap3DList.push(value);
	}

	addFilter(value){
		this.filterList.push(value);
	}
}

module.exports = DrawUnitCollector2D;
