'use strict';

class DrawUnitCollector3D
{
	constructor(){
		this.entityList = [];
		this.lightList = [];
		this.shadowCasterList = [];
		this.mirrorList = [];
	}

	clear(){
		this.entityList.length = 0;
		this.lightList.length = 0;
		this.shadowCasterList.length = 0;
		this.mirrorList.length = 0;
	}

	clone(){
		const copy = new this.constructor();
		copy.entityList.push(...this.entityList);
		copy.lightList.push(...this.lightList);
		copy.shadowCasterList.push(...this.shadowCasterList);
		copy.mirrorList.push(...this.mirrorList);
		return copy;
	}

	addEntity(value){
		this.entityList.push(value);
	}

	addLight(value){
		this.lightList.push(value);
	}

	addShadowCaster(value){
		this.shadowCasterList.push(value);
	}

	addMirror(value){
		this.mirrorList.push(value);
	}
}

module.exports = DrawUnitCollector3D;
