'use strict';

class DrawUnitCollector3D
{
	constructor(){
		this.entityList = [];
		this.lightList = [];
		this.shadowCasterList = [];
	}

	clear(){
		this.entityList.length = 0;
		this.lightList.length = 0;
		this.shadowCasterList.length = 0;
	}

	addEntity(value){
		this.entityList.push(value);
	}

	addLight(value){
		this.lightList.push(value);
	}

	addShadowCaster(value){
		this.shadowCasterList.push(value);
	}
}

module.exports = DrawUnitCollector3D;
