'use strict';

const Vulkan = require('../Vulkan');
const Matrix2D = require('opengl/math/Matrix2D');

class Filter2D
{
	static get textureBindGroup(){
		if(!this._textureBindGroup){
			const {device, filterSwapChain} = Vulkan;
			this._textureBindGroup = device.createBindGroup({
				layout: Vulkan.bindGroupLayoutDict.bindGroupLayoutFilter2D,
				entries: [{
					binding: 0,
					resource: device.createSampler({
						magFilter: 'linear',
						minFilter: 'linear',
					})
				},{	binding: 1,
					resource: filterSwapChain.frameBufferA.createView()
				}]
			});
		}
		return this._textureBindGroup;
	}

	constructor(){
		this.worldTransform = new Matrix2D();
	}

	onDraw(){}
}

module.exports = Filter2D;
