'use strict';

const Vulkan = require('../Vulkan');
const {Texture2D} = require('opengl/textures');

class Filter2D
{
	static Subclass(pipelineDesc){
		const Sub = class extends this {};
		Vulkan.bindRenderPipeline(Sub.prototype, pipelineDesc);
		return Sub;
	}

	constructor(){
		this.texture = new Texture2D();
	}

	onDraw(){}
}

module.exports = Filter2D;
