'use strict';

class IndexDistributor
{
	constructor(){
		this.map = new Map();
	}

	getIndex(key){
		const {map} = this;
		if(map.has(key))return map.get(key);
		const result = map.size;
		map.set(key, result);
		return result;
	}

	reset(){
		this.map.clear();
	}
}

module.exports = IndexDistributor;
