'use strict';

const vertexShaderGLSL = `#version 450
	layout(set = 0, binding = 0) uniform Uniforms {
		mat4 modelViewProjectionMatrix;
	} uniforms;
	const vec2 pos[3] = vec2[3](vec2(0.0f, 0.5f), vec2(-0.5f, -0.5f), vec2(0.5f, -0.5f));
	void main() {
	  gl_Position = vec4(pos[gl_VertexIndex], 0.0, 1.0);
	}
`;

const fragmentShaderGLSL = `#version 450
	layout(location = 0) out vec4 outColor;
	void main() {
		outColor = vec4(1.0, 0.0, 0.0, 1.0);
	}
`;

async function loadImage(path){
	let image = new Image();
	image.src = path;
	await image.decode();
	return image;
}

async function init(){
	const glslangModule = await import('https://unpkg.com/@webgpu/glslang@0.0.7/web/glslang.js');
	const glslang = await glslangModule.default();
	
	const canvas = document.createElement('canvas');
	document.body.appendChild(canvas);
	
	if(!navigator.gpu)return;
	const adapter = await navigator.gpu.requestAdapter();
	const device = await adapter.requestDevice();
	const context = canvas.getContext('gpupresent');
	const swapChainFormat = await context.getSwapChainPreferredFormat(device);
	const swapChain = context.configureSwapChain({
		device,
		format: swapChainFormat,
		usage: GPUTextureUsage.OUTPUT_ATTACHMENT
	});

	const uniformBuffer = device.createBuffer({
		size: 4096,
		usage: GPUBufferUsage.UNIFORM | GPUBufferUsage.COPY_DST
	});

	const imageBitmap = await createImageBitmap(await loadImage('./1.png'));

	const texture = device.createTexture({
		size: [imageBitmap.width, imageBitmap.height, 1],
		format: swapChainFormat,
		usage: GPUTextureUsage.SAMPLED,
	});

	device.copyImageBitmapToTexture(
		{imageBitmap, origin:[0, 0]},
		{texture, mipLevel: 0, arrayLayer: 0, origin: [0, 0, 0]},
		[imageBitmap.width, imageBitmap.height, 1]
	);

	const bindGroupLayout = device.createBindGroupLayout({
		bindings:[{
			binding: 0,
			visibility: GPUShaderStage.VERTEX,
			type: 'uniform-buffer',
		}/*,{
			binding: 1,
			visibility: GPUShaderStage.FRAGMENT,
			type: 'sampled-texture'
		}*/]
	});

	const bindGroup = device.createBindGroup({
		layout: bindGroupLayout,
		bindings: [{
			binding: 0,
			resource: {
				buffer: uniformBuffer,
				offset: 0
			}
		}/*,{
			binding: 1,
			resource: texture.createView()
		}*/]
	});

	const pipelineLayout = device.createPipelineLayout({
		bindGroupLayouts: [bindGroupLayout]
	});

	const pipeline = device.createRenderPipeline({
		layout: pipelineLayout,
		vertexStage: {
			module: device.createShaderModule({
				code: glslang.compileGLSL(vertexShaderGLSL, "vertex"),
			}),
			entryPoint: 'main'
		},
		fragmentStage: {
			module: device.createShaderModule({
				code: glslang.compileGLSL(fragmentShaderGLSL, "fragment"),
			}),
			entryPoint: 'main'
		},
		primitiveTopology: 'triangle-list',
		colorStates:[{
			format: swapChainFormat
		}],
		/*vertexState: {
			indexFormat: 'uint16',
			vertexBuffers:[{
				arrayStride: 32,
				stepMode: 'vertex',
				attributes:[{
					format: GPUVertexFormat.float4,
					offset: 0,
					shaderLocation: 1
				}]
			}]
		}*/
	});

	const depthTexture = device.createTexture({
		size: [canvas.width, canvas.height, 1],
		format: 'depth24plus-stencil8',
		usage: GPUTextureUsage.OUTPUT_ATTACHMENT,
		dimension: '2d',//optional, default 2d(1d, 3d)
	});

	const depthStencilAttachmentDescriptor = {
		attachment: depthTexture.createView(),
		depthLoadValue: 1.0,
		depthStoreOp: 'store',
		stencilLoadValue: 0,
		stencilStoreOp: 'store'
	};
	
	function frame(){
		requestAnimationFrame(frame);
		const renderBundleEncoder = device.createRenderBundleEncoder({
			colorFormats:[swapChainFormat],
			depthStencilFormat: 'depth24plus-stencil8'
		});
		const commandEncoder = device.createCommandEncoder();
		const renderPassEncoder = commandEncoder.beginRenderPass({
			colorAttachments: [{
				attachment: swapChain.getCurrentTexture().createView(),
				loadValue: [0, 0, 0, 1]
			}],
			depthStencilAttachment: depthStencilAttachmentDescriptor//when 2D, can omit
		});
		renderBundleEncoder.setPipeline(pipeline);
		renderBundleEncoder.setBindGroup(0, bindGroup);
		renderBundleEncoder.draw(3, 1, 0, 0);
		const renderBundle = renderBundleEncoder.finish();
		renderPassEncoder.executeBundles([renderBundle]);
		renderPassEncoder.endPass();

		device.defaultQueue.submit([commandEncoder.finish()]);
	}
	requestAnimationFrame(frame);
}
