"use strict";

const DisplayObject3D = require('../DisplayObject3D');


class Light3D extends DisplayObject3D
{
	constructor(){
		super();
		this.castShadow = false;
	}
}

Light3D.prototype.drawDepth = function(){
	const groupFn = drawUnit => drawUnit.material.depth?.constructor;
	return function(renderPassEncoder, entityList){
		for(let [materialType, drawUnitList] of Map.groupBy(entityList, groupFn)){
			materialType?.draw(drawUnitList, renderPassEncoder);
		}
	}
}();

module.exports = Light3D;