"use strict";

const {loadImage} = require('../WebGL');

class AssetManager{
	constructor(){
	}

	loadFiles(fileList){
		return Promise.all(fileList.map(this.loadFile));
	}

	loadFile(file){
		return fetch(`/assets/${file}`).then(v => v.arrayBuffer());
	}

	loadImage(name){
		return loadImage(`../assets/${name}`);
	}
}

module.exports = AssetManager;
