'use strict';

const Vulkan = require('../Vulkan');
const Material = require('./Material');

const {delayNew} = require('utils/cache');
const transformSlot = require('./slots/transformSlot');

class BillboardMaterial extends Material
{
	static useMeshFlag = false;
	static get pipeline(){
		return Vulkan.pipelineDict.pipeline3DBillboard;
	}
	static bindGroupSlots = delayNew(() => [transformSlot()]);
	static bindIndexList = [1];
	static doDraw(instanceCount){
		const {renderPassEncoder} = Vulkan;
		renderPassEncoder.draw(4, instanceCount);
	}
	constructor(size=100){
		super();
		this.size = size;
	}
}

module.exports = BillboardMaterial;
