'use strict';

const Vulkan = require('../Vulkan');
const Material = require('./Material');

const transformSlot = require('./slots/transformSlot');
const boundSlot = require('./slots/boundSlot');

const pipelineDesc = {
	layout: ['0', '2_dynamic'],
	vertex:{
		module: 'vertex_bound',
	},
	fragment:{
		module: 'fragment_bound',
		targets:[{
			format: 'bgra8unorm',
			blend: ['transparent']
		}]
	},
	depthStencil: {
		format: 'depth24plus-stencil8',
		depthWriteEnabled: true,
		depthCompare: 'less-equal',
	},
	primitive: {
		topology: 'line-list',
	}
};

class BoundMaterial extends Material.Subclass(
	pipelineDesc,
	[null, boundSlot]
){
	static useMeshFlag = false;
	static onDraw(instanceCount){
		Vulkan.renderPassEncoder.draw(24, instanceCount);
	}
	constructor(bound){
		super();
		this.bound = bound;
	}
}

module.exports = BoundMaterial;
