'use strict';

const Vulkan = require('../Vulkan');
const Material = require('./Material');

const {delayNew} = require('utils/cache');
const transformSlot = require('./slots/transformSlot');

class BoundMaterial extends Material
{
	static useMeshFlag = false;
	static get pipeline(){
		return Vulkan.pipelineDict.pipelineBound;
	}
	static bindGroupSlots = delayNew(() => [transformSlot()]);
	static onDraw(entityList, infoList){
		const {renderPassEncoder, currentMesh} = Vulkan;
		for(const [index, offset, count] of infoList){
			renderPassEncoder.setBindGroup(1, transformSlot().getBindGroup(index), [0]);
			renderPassEncoder.draw(24, 1, 0, offset);
		}
	}
	constructor(bound){
		super();
		this.bound = bound;
	}
}

module.exports = BoundMaterial;
