'use strict';

const TextureMaterial = require('./TextureMaterial');
const {colorTextureCache} = require('../Vulkan/resource');

class ColorMaterial extends TextureMaterial{
	static maxCountPerDraw = 1;

	static onFetchTexture(texture, material, renderPassEncoder){
		return renderPassEncoder.loadTexture(material.color, false);
	}

	constructor(color, subMeshFilter=0xFFFFFFFF){
		super(subMeshFilter);
		this.color = color;
	}
}

module.exports = ColorMaterial;
