'use strict';

const TextureMaterial = require('./TextureMaterial');

const pipeline = TextureMaterial.pipeline.clone({
	fragment:{
		targets:[{
			format: 'bgra8unorm',
			blend: 'one add one-minus-src;src-alpha add one-minus-src-alpha'
		}],
	},
	depthStencil:{
		depthWriteEnabled: false
	}
});

class GlowTextureMaterial extends TextureMaterial.Subclass(pipeline){
	static order = 1;
}

module.exports = GlowTextureMaterial;
