'use strict';

const TextureMaterial = require('./TextureMaterial');
const Pipeline = require('./Pipeline');

const pipeline = new Pipeline({
	...TextureMaterial.pipeline.desc,
	fragment:{
		code: TextureMaterial.pipeline.desc.fragment.code,
		targets:[{blend: 'one add one-minus-src;src-alpha add one-minus-src-alpha'}]
	},
	depthStencil:{depthWriteEnabled: false}
});

class GlowTextureMaterial extends TextureMaterial.Subclass(pipeline){
	static order = 1;
}

module.exports = GlowTextureMaterial;
