'use strict';

const TextureMaterial = require('./TextureMaterial');

const pipeline = {
	...TextureMaterial.pipelineDesc,
	fragment: {
		...TextureMaterial.pipelineDesc.fragment,
		targets:[{
			format: 'bgra8unorm',
			blend: {
				color:{srcFactor: 'one', dstFactor: 'one-minus-src', operation: 'add'},
				alpha:{srcFactor: 'src-alpha', dstFactor: 'one-minus-src-alpha', operation: 'add'}
			}
		}],
	},
	depthStencil: {
		...TextureMaterial.pipelineDesc.depthStencil,
		depthWriteEnabled: false,
	}
};

class GlowTextureMaterial extends TextureMaterial.Subclass({pipeline}){
	static order = 1;
}

module.exports = GlowTextureMaterial;
