'use strict';

const Vulkan = require('../Vulkan');
const Material = require('./Material');
const {classifyTexture} = require('./helper');

const pipelineDesc = {
	layout: ['0', '5', '2_dynamic'],
	vertex: {
		module: 'vertex_terrain',
		buffers: [{
			name: 'layer0',
			arrayStride: 12,
			stepMode: 'vertex',
			attributes: ['uint8x2', {offset:4, format:'unorm8x2'}, {offset:8, format:'unorm8x4'}]
		}]
	},
	fragment: {
		module: 'fragment_terrain',
		targets:[{
			format: 'bgra8unorm',
			blend: ['transparent']
		}]
	},
	depthStencil: {
		format: 'depth24plus-stencil8',
		depthWriteEnabled: true,
		depthCompare: 'less-equal',
	}
};

class TerrainMaterial extends Material.Subclass(
	pipelineDesc,
	{transform:2}
){
	static *calcDrawIterator(entityList, mesh){
		let subMesh = mesh.subMeshList[0];
		subMesh.setVertexBuffer(this.vertexBuffers);
		subMesh.setIndexBuffer();
		for(const [texture, subEntityList] of classifyTexture(entityList)){
			Vulkan.renderPassEncoder.setBindGroup(1, Vulkan.createBindGroup('5', texture));
			yield * super.calcDrawIterator(subEntityList);
		}
	}
	static onDraw(instanceCount, mesh){
		let subMesh = mesh.subMeshList[0];
		subMesh.drawIndexed(instanceCount);
	}
	constructor(texture){
		super();
		this.texture = texture;
	}
}

module.exports = TerrainMaterial;
