'use strict';

const Vulkan = require('../Vulkan');
const Material = require('./Material');

const groupDraw = require('../helper/groupDraw');

const pipelineDesc = {
	layout: ['0', '2_dynamic', '2_dynamic', '3'],
	vertex: {
		module: 'vertex_3d',
		buffers: [{
			name: 'position',
			arrayStride: 3 << 2,
			stepMode: 'vertex',
			attributes: [{shaderLocation:0, format:'float32x3'}]
		}, {
			name: 'normal',
			arrayStride: 3 << 2,
			stepMode: 'vertex',
			attributes: [{shaderLocation:1, format:'float32x3'}]
		}, {
			name: 'uv',
			arrayStride: 2 << 2,
			stepMode: 'vertex',
			attributes: [{shaderLocation:2, format:'float32x2'}]
		}, {
			name: 'boneIndex',
			arrayStride: 1 << 2,
			stepMode: 'vertex',
			attributes: [{shaderLocation:3, format:'uint32'}]
		}]
	},
	fragment: {
		module: 'fragment_3d',
		targets:[{
			format: 'bgra8unorm',
			blend: ['transparent']
		}]
	},
	depthStencil: {
		format: 'depth24plus-stencil8',
		depthWriteEnabled: true,
		depthCompare: 'less-equal',
	}
};

class TextureMaterial extends Material.Subclass(
	pipelineDesc,
	{transform:1, bone:2}
){
	static *calcDrawIterator(entityList, mesh){
		const usageMax = (1 << entityList.length) - 1;
		const iterator = super.calcDrawIterator(entityList).next().value;
		for(let i=0, n=mesh.subMeshList.length; i<n; ++i){
			const subMesh = mesh.subMeshList[i];
			if(!subMesh.texture)continue;
			const texture = Vulkan.fetchTexture(subMesh.texture);
			if(!texture)continue;
			const usage = calcMaterialUsage(entityList, 1 << i);
			if(usage == 0)continue;
			Vulkan.renderPassEncoder.setBindGroup(3, Vulkan.createBindGroup('3', texture.createView()));
			this.directDrawFlag = usageMax == usage;
			this.subMesh = subMesh;
			this.usage = usage;
			subMesh.setVertexBuffer(this.vertexBuffers);
			yield iterator;
		}
	}
	static onDraw(instanceCount){
		if(this.directDrawFlag){
			this.subMesh.draw(instanceCount);
			return;
		}
		for(const [offset, count] of this.groupDraw(instanceCount)){
			this.subMesh.draw(count, offset);
		}
		this.usage >>= instanceCount;
	}
	static groupDraw = groupDraw.bind(null, i => this.usage & (1 << i));

	constructor(subMeshFilter=0xFFFFFFFF){
		super();
		this.subMeshFilter = subMeshFilter;
	}
}

function calcMaterialUsage(entityList, mask){
	let value = 0;
	for(let i=0, n=entityList.length; i<n; ++i){
		if(entityList[i].material.subMeshFilter & mask){
			value |= 1 << i;
		}
	}
	return value;
}

module.exports = TextureMaterial;
