'use strict';

const Vulkan = require('../Vulkan');
const Material = require('./Material');

const groupDraw = require('../helper/groupDraw');

const pipelineDesc = {
	layout: ['0', '2_dynamic', '2_dynamic', '3'],
	vertex: {
		module: 'vertex_3d',
		buffers: [{
			name: 'position',
			arrayStride: 3 << 2,
			stepMode: 'vertex',
			attributes: [{shaderLocation:0, format:'float32x3'}]
		}, {
			name: 'normal',
			arrayStride: 3 << 2,
			stepMode: 'vertex',
			attributes: [{shaderLocation:1, format:'float32x3'}]
		}, {
			name: 'uv',
			arrayStride: 2 << 2,
			stepMode: 'vertex',
			attributes: [{shaderLocation:2, format:'float32x2'}]
		}, {
			name: 'boneIndex',
			arrayStride: 1 << 2,
			stepMode: 'vertex',
			attributes: [{shaderLocation:3, format:'uint32'}]
		}]
	},
	fragment: {
		module: 'fragment_3d',
		targets:[{
			format: 'bgra8unorm',
			blend: ['transparent']
		}]
	},
	depthStencil: {
		format: 'depth24plus-stencil8',
		depthWriteEnabled: true,
		depthCompare: 'less-equal',
	}
};

class TextureMaterial extends Material.Subclass(
	pipelineDesc,
	{transform:1, bone:2}
){
	static usageList = [];
	static *calcDrawIterator(entityList, mesh){
		const subMeshCount = mesh.subMeshList.length;
		let offset = 0;
		for(let value of super.calcDrawIterator(entityList)){
			for(let i=0; i<subMeshCount; ++i){
				this.usageList[i] = calcMaterialUsage(entityList, offset, value, 1 << i);
			}
			offset += value;
			yield value;
		}
	}

	static onDrawSubMesh(instanceCount, subMesh, i){
		if(!subMesh.texture)return;
		const texture = Vulkan.fetchTexture(subMesh.texture);
		if(!texture)return;
		const usage = this.usageList[i];
		if(usage == 0)return;
		Vulkan.renderPassEncoder.setBindGroup(3, Vulkan.createBindGroup('3', texture.createView()));
		subMesh.setBuffers(this.vertexBuffers);
		for(const [offset, count] of groupDraw(i => usage & 1 << i, instanceCount)){
			subMesh.draw(count, offset);
		}
	}

	constructor(subMeshFilter=0xFFFFFFFF){
		super();
		this.subMeshFilter = subMeshFilter;
	}
}

function calcMaterialUsage(entityList, offset, count, mask){
	let value = 0;
	for(let i=0; i<count; ++i){
		if(entityList[offset+i].material.subMeshFilter & mask){
			value |= 1 << i;
		}
	}
	return value;
}

module.exports = TextureMaterial;
