'use strict';

const TextureMaterial = require('./TextureMaterial');

const pipeline = TextureMaterial.pipeline.clone({
	vertex: {entryPoint: 'mainDeferred'},
	fragment:{
		entryPoint: 'mainDeferred',
		targets:[{format: 'bgra8unorm'}, {writeMask:7,format: 'rgba16float'},{format: 'bgra8unorm'}]
	}
});

module.exports = TextureMaterial.Subclass(pipeline);
