'use strict';

const Vulkan = require('../Vulkan');
const TextureMaterial = require('./TextureMaterial');
const {attributes, blend} = require('./helper');

const pipelineDesc = {
	layout: ['0', '2_dynamic', '2_dynamic', '3'],
	vertex: {
		module: 'vertex_3d',
		buffers: [{
			arrayStride: 9 << 2,
			stepMode: 'vertex',
			attributes: attributes('float32x3', 'float32x3', 'float32x2', 'uint32')
		}]
	},
	fragment: {
		module: 'fragment_3d',
		targets:[{
			format: 'bgra8unorm',
			blend: blend(['one', 'one-minus-src-color'], 'transparent')
		}]
	},
	depthStencil: {
		format: 'depth24plus-stencil8',
		depthWriteEnabled: false,
		depthCompare: 'less-equal',
	}
};

class WingTextureMaterial extends TextureMaterial.Subclass(
	pipelineDesc,
){
}

module.exports = WingTextureMaterial;
