'use strict';

const Vulkan = require('../Vulkan');
const Material = require('./Material');



class WireframeMaterial extends Material
{
	static get pipeline(){
		return Vulkan.pipelineDict.pipelineWireframe;
	}
	static onDraw(entityList, infoList){
		const {transformUniformCache, boneDataCache} = Vulkan.transformUniformMgr;
		const {renderPassEncoder, currentMesh:mesh} = Vulkan;
		for(const [index, offset, count] of infoList){
			renderPassEncoder.setBindGroup(1, transformUniformCache(index));
			renderPassEncoder.setBindGroup(2, boneDataCache(index));
			renderPassEncoder.drawIndexed(mesh.wireframeIndexCount, count, 0, 0, offset);
		}
	}
	static setIndexBuffer(){
		const {renderPassEncoder, currentMesh:mesh} = Vulkan;
		if(!mesh._wireframeIndexBuffer){
			const rawData = mesh.indexData;
			const indexCount = rawData ? rawData.length : mesh.vertexCount;
			const indexData = new Uint16Array(indexCount << 1);
			if(rawData){
				for(let i=0; i<indexCount; i+=3){
					indexData.set([rawData[i], rawData[i+1], rawData[i+1], rawData[i+2], rawData[i+2], rawData[i]], i << 1);
				}
			}else{
				for(let i=0; i<indexCount; i+=3){
					indexData.set([i, i+1, i+1, i+2, i+2, i], i << 1);
				}
			}
			mesh.wireframeIndexCount = indexData.length;
			mesh._wireframeIndexBuffer = Vulkan.createBufferMapped(GPUBufferUsage.INDEX, indexData);
		}
		renderPassEncoder.setIndexBuffer(mesh._wireframeIndexBuffer);
	}
}

module.exports = WireframeMaterial;
